import MaterialButton from "@material-ui/core/Button";
import { Layout, Menu, Icon } from "antd";
import update from "immutability-helper";
import { NextPage } from "next";
import * as React from "react";
import { useState } from "react";
import { Dispatch } from "react";
import { SetStateAction } from "react";
import { DndProvider } from "react-dnd";
import Backend from "react-dnd-html5-backend";
import DnDContainer from "~/components/DnDContainer";
import LeftSider from "~/components/LeftSider";
import RightSider from "~/components/RightSider";
import componentGroups from "~/data/materialComponents";
import { TComponent, TProp } from "~/interfaces/menu";
import DefaultLayout from "~/layouts/default";

const { SubMenu } = Menu;
const { Sider } = Layout;

export type TBox = {
  top: number;
  left: number;
  jsx: JSX.Element;
  componentName: TComponent["title"];
};
export type TBoxes = {
  [key: string]: TBox;
};

export type IndexPageState = {
  boxes: TBoxes;
};

const IndexPage: NextPage = () => {
  const [boxes, setBoxes]: [
    TBoxes,
    Dispatch<SetStateAction<TBoxes>>
  ] = useState<TBoxes>({
    a: {
      top: 20,
      left: 80,
      jsx: (
        <MaterialButton variant="contained" color="primary">
          button1
        </MaterialButton>
      ),
      componentName: "Button"
    },
    b: {
      top: 180,
      left: 20,
      jsx: (
        <MaterialButton variant="contained" color="secondary">
          button2
        </MaterialButton>
      ),
      componentName: "Button"
    }
  });

  const [selectedComponentName, setSelectedComponentName]: [
    TComponent["title"],
    Dispatch<SetStateAction<TComponent["title"]>>
  ] = useState<TComponent["title"]>("Button");

  const selectedComponentProps = (
    componentName: TComponent["title"]
  ): TProp[] => {
    return componentGroups.forEach(componentGroup => {
      return componentGroup.components.forEach(
        component => {
          if (component.title === componentName) {
            return component.props;
          }
        }
      );
    });
  };

  const makeId = (length: number): string => {
    let result = "";
    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    const charactersLength = characters.length;
    for (let i = 0; i < length; i++) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  };

  const addDnDBox = (): void => {
    const key = makeId(5);
    setBoxes(
      update(boxes, {
        // TODO: この部分、型指定が出来ないの、よろしくないのでは？
        $merge: {
          [key]: {
            top: 180,
            left: 20,
            jsx: (
              <MaterialButton variant="contained" color="secondary">
                ほげほげ
              </MaterialButton>
            ),
            componentName: "Button"
          }
        }
      })
    );
  };

  return (
    <DefaultLayout>
      <Layout>
        <LeftSider subMenus={componentGroups} />
        <DndProvider backend={Backend}>
          <DnDContainer
            hideSourceOnDrag={true}
            boxes={boxes}
            setBoxes={setBoxes}
          />
        </DndProvider>
        <RightSider menuItems={selectedComponentProps} />
      </Layout>
    </DefaultLayout>
  );
};

export default IndexPage;
