import { Icon, Menu, Layout, Input } from "antd";
import * as React from "react";
import { TComponent } from "~/interfaces/menu";

const { SubMenu } = Menu;
const { Sider } = Layout;

export type RightSiderProps = {
  subMenus: TComponent["props"];
};

const RightSider: React.FC<RightSiderProps> = ({ subMenus }) => {
  return (
    <Sider width={200} style={{ background: "#fff" }}>
      <Menu
        mode="inline"
        defaultSelectedKeys={["1"]}
        defaultOpenKeys={["sub1"]}
        style={{ height: "100%", borderRight: 0 }}
      >
          <SubMenu
            key={subMenuIndex}
            title={
              <span>
                <Icon type={subMenu.iconType} />
                プロパティ
              </span>
            }
          >
            {subMenu.menuItems.map(
              (item: TRightMenuItem, itemIndex: number) => (
                <Menu.Item key={`${subMenuIndex}-${itemIndex}`}>
                  <Input placeholder="プレースホルダー" />
                </Menu.Item>
              )
            )}
          </SubMenu>
      </Menu>
    </Sider>
  );
};

export default RightSider;
