import { Icon, Menu, Layout, Input } from "antd";
import * as React from "react";
import { TComponent, TProp } from "~/interfaces/menu";

const { SubMenu } = Menu;
const { Sider } = Layout;

export type RightSiderProps = {
  menuItems: TComponent["props"] | void;
};

const RightSider: React.FC<RightSiderProps> = ({ menuItems }) => {
  return (
    <Sider width={200} style={{ background: "#fff" }}>
      <Menu
        mode="inline"
        defaultSelectedKeys={["1"]}
        defaultOpenKeys={["sub1"]}
        style={{ height: "100%", borderRight: 0 }}
      >
        <SubMenu
          key="hogehoge" // TODO 直す
          title={
            <span>
              <Icon type="user" />
              プロパティ
            </span>
          }
        >

          {menuItems.map((componentProp: TProp, index: number) => (
            <Menu.Item key={index}>
              <Input placeholder={componentProp.name} />
            </Menu.Item>
          ))}
        </SubMenu>
      </Menu>
    </Sider>
  );
};

export default RightSider;
