import * as React from "react";
import { Layout, Menu, Icon } from "antd";
import DefaultLayout from "../layouts/default";
import { NextPage } from "next";

const { SubMenu } = Menu;
const { Sider } = Layout;

type TMenuItem = {
  title: string;
};
type TSubMenu = {
  key: string;
  iconType: string;
  title: string;
  menuItems: TMenuItem[];
};

const IndexPage: NextPage = () => {
  const subMenus: TSubMenu[] = [
    {
      key: "sub1",
      iconType: "user",
      title: "subnav 1",
      menuItems: [
        { title: "option1" },
        { title: "option2" },
        { title: "option3" }
      ]
    },
    {
      key: "sub2",
      iconType: "laptop",
      title: "subnav 2",
      menuItems: [
        { title: "option4" },
        { title: "option5" },
        { title: "option6" }
      ]
    },
    {
      key: "sub3",
      iconType: "notification",
      title: "subnav 3",
      menuItems: [
        { title: "option7" },
        { title: "option8" },
        { title: "option9" }
      ]
    }
  ];

  return (
    <DefaultLayout>
      <Layout>
        <Sider width={200} style={{ background: "#fff" }}>
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{ height: "100%", borderRight: 0 }}
          >
            {subMenus.map(subMenu => (
              <SubMenu
                key={subMenu.key}
                title={
                  <span>
                    <Icon type={subMenu.iconType} />
                    {subMenu.title}
                  </span>
                }
              >
                {subMenu.menuItems.map((item, itemIndex) => (
                  <Menu.Item key={`${subMenu.key}-${itemIndex}`}>{item.title}</Menu.Item>
                ))}
              </SubMenu>
            ))}
          </Menu>
        </Sider>
      </Layout>
    </DefaultLayout>
  );
};

export default IndexPage;
