import * as React from "react";
import { useState } from "react";
import { Layout, Menu, Icon } from "antd";
import DefaultLayout from "~/layouts/default";
import { NextPage } from "next";
import { TMenuItem, TSubMenu } from "~/interfaces/menu";
import subMenus from "~/data/subMenus";
import { DndProvider } from "react-dnd";
import Backend from "react-dnd-html5-backend";
import DnDContainer from "~/components/DnDContainer";
import update from "immutability-helper";
import MaterialButton from "@material-ui/core/Button";

const { SubMenu } = Menu;
const { Sider } = Layout;

export type TBox = {
  top: number;
  left: number;
  width: number;
  height: number;
  title: string;
  jsx: JSX.Element; // TODO 直す！！！！！！
};

export type IndexPageState = {
  boxes: {
    [key: string]: TBox;
  };
};

const IndexPage: NextPage = () => {
  const [boxes, setBoxes] = useState<{
    [key: string]: TBox;
  }>({
    a: {
      top: 20,
      left: 80,
      width: 100,
      height: 50,
      title: "Drag me around",
      jsx: <Button variant="contained" color="primary">hogehoeg</Button>
    },
    b: {
      top: 180,
      left: 20,
      width: 130,
      height: 50,
      title: "Drag me too",
      jsx: <h2>fugafuga</h2>
    }
  });

  const makeId = (length: number): string => {
    let result = "";
    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    const charactersLength = characters.length;
    for (let i = 0; i < length; i++) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  };

  const addDnDBox = (title: string): void => {
    const key = makeId(5);
    setBoxes(
      update(boxes, {
        $merge: {
          [key]: {
            top: 180,
            left: 20,
            width: 120,
            height: 30,
            title,
            jsx: <h3>hogehoge</h3>
          }
        }
      })
    );
  };

  return (
    <DefaultLayout>
      <Layout>
        <Sider width={200} style={{ background: "#fff" }}>
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{ height: "100%", borderRight: 0 }}
          >
            {subMenus.map((subMenu: TSubMenu, subMenuIndex: number) => (
              <SubMenu
                key={subMenuIndex}
                title={
                  <span>
                    <Icon type={subMenu.iconType} />
                    {subMenu.title}
                  </span>
                }
              >
                {subMenu.menuItems.map((item: TMenuItem, itemIndex: number) => (
                  <Menu.Item
                    key={`${subMenuIndex}-${itemIndex}`}
                    onClick={(): void => addDnDBox(item.title)}
                  >
                    {item.title}
                  </Menu.Item>
                ))}
              </SubMenu>
            ))}
          </Menu>
        </Sider>
        <DndProvider backend={Backend}>
          <DnDContainer
            hideSourceOnDrag={true}
            boxes={boxes}
            setBoxes={setBoxes}
          />
        </DndProvider>
      </Layout>
    </DefaultLayout>
  );
};

export default IndexPage;
