import * as React from "react";
import { Layout, Menu, Icon } from "antd";
import DefaultLayout from "../layouts/default";
import { NextPage } from "next";

const { SubMenu } = Menu;
const { Sider } = Layout;

type MenuItem = {
  title: string
}
type SubMenu = {
  key: string;
  iconType: string;
  title: string;
  menuItems: MenuItem[];
};

const subMenus: SubMenu[] = [
  {
    key: "sub1",
    iconType: "user",
    title: "subnav 1",
    menuItems: [
      { title: "option1" },
      { title: "option2" },
      { title: "option3" }
    ]
  },
  {
    key: "sub2",
    iconType: "laptop",
    title: "subnav 2",
    menuItems: [
      { title: "option4" },
      { title: "option5" },
      { title: "option6" }
    ]
  },
  {
    key: "sub3",
    iconType: "notification",
    title: "subnav 3",
    menuItems: [
      { title: "option7" },
      { title: "option8" },
      { title: "option9" }
    ]
  }
];

const IndexPage: NextPage = () => {
  return (
    <DefaultLayout>
      <Layout>
        <Sider width={200} style={{ background: "#fff" }}>
          <Menu
            mode="inline"
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            style={{ height: "100%", borderRight: 0 }}
          >
            {subMenus.map(subMenu => (
              <SubMenu
                key={subMenu.key}
                title={
                  <span>
                    <Icon type={subMenu.iconType} />
                    {subMenu.title}
                  </span>
                }
              >
                {subMenu.menuItems.map((item, itemIndex) => (
                  <Menu.Item key={itemIndex}>{item.title}</Menu.Item>
                ))}
              </SubMenu>
            ))}
          </Menu>
        </Sider>
      </Layout>
    </DefaultLayout>
  );
};

export default IndexPage;
